mapper.select:
select store_id_, sum(active_quantity_) as active_quantity_, sum(archived_quantity_) as archived_quantity_, sum(pending_quantity_) as pending_quantity_, sum(disapproved_quantity_) as disapproved_quantity_ from (select store_id_, quantity_ as active_quantity_, 0 as archived_quantity_, 0 as pending_quantity_, 0 as disapproved_quantity_ from mf_dw_product_quantity_fact where status_id_ = 'active' and store_id_ = #{storeId}
union all
select store_id_, 0 as active_quantity_, quantity_ as archived_quantity_, 0 as pending_quantity_, 0 as disapproved_quantity_ from mf_dw_product_quantity_fact where status_id_ = 'archived' and store_id_ = #{storeId}
union all
select store_id_, 0 as active_quantity_, 0 as archived_quantity_, quantity_ as pending_quantity_, 0 as disapproved_quantity_ from mf_dw_product_quantity_fact where status_id_ = 'pending' and store_id_ = #{storeId}
union all
select store_id_, 0 as active_quantity_, 0 as archived_quantity_, 0 as pending_quantity_, quantity_ as disapproved_quantity_ from mf_dw_product_quantity_fact where status_id_ = 'disapproved' and store_id_ = #{storeId}) quantity group by store_id_
