mapper.update:
UPDATE customer_browse_product
SET
    browsing_time_ = #{browsingTime}
WHERE
    customer_id_ = #{customerId} AND product_id_ = #{productId}

mapper.select:
SELECT customer_id_, product_id_, browsing_time_ FROM customer_browse_product
AND customer_id_ = #{customerId}
AND browsing_time_ = #{browsingTime}

SELECT
count(*)
FROM
customer_browse_product


        AND customer_id_ = #{customerId}


        AND browsing_time_ = #{browsingTime}
